SELECT * FROM STUDENT group by id having id in(SELECT STUDENT_ID FROM MARK WHERE MARK>8);
SELECT student.ID, student.NAME FROM STUDENT INNER JOIN MARK ON STUDENT.ID=MARK.STUDENT_ID GROUP BY STUDENT.ID HAVING MIN(MARK)>7;
SELECT student.ID, student.NAME FROM STUDENT INNER JOIN PAYMENT ON STUDENT.ID=PAYMENT.STUDENT_ID group by STUDENT.ID, STUDENT.NAME HAVING COUNT(PAYMENT.STUDENT_ID)>2 AND YEAR(PAYMENT.payment_date)=2019;